params.styles:
.main-table .ant-table-header { overflow: auto !important } .main-table .ant-table-body { overflow-y: auto !important }

th.billing-history-accountBalance span.ant-table-header-column, td.billing-history-accountBalance .dateTime{}

.ant-table-tbody > tr:hover:not(.ant-table-expanded-row):not(.ant-table-row-selected) > td.billing-history-accountBalance .dateTime{}


/* Tab content background color */
.billing-history .ant-tabs-bar.ant-tabs-top-bar {
    background-color: #FFFFFF;
    margin: 0px;
    height: 44px;
}

.billing-history  .billing-history .tab-content{
  color: #404041;
}

/* Taargeting slight gap beteen tab and tab pane */
.ant-tabs .ant-tabs-top-content > .ant-tabs-tabpane{
  margin-top: -1px;
}

/* Border around tab content */
.customer-360-billing-history .billing-history .ant-tabs.ant-tabs-top.billing-history-accordian.ant-tabs-line.ant-tabs-no-animation {
    border: 8px solid #BBBDC0;
    border-top: none;
}

/* Active bottom line */
.billing-history .ant-tabs-ink-bar{
  background: transparent;
}

/* targeting Tab pan */
.billing-history .billing-history-accordian .tab-content{
  display: flex;
  flex-direction: row; 
  width: 100%;
  padding: 0px 8px;
  justify-content: space-between;
  border-bottom: 1px solid #BBBDC0;

}

.billing-history .billing-history-accordian .ant-tabs-nav .ant-tabs-tab-active .tab-content,
.billing-history .billing-history-accordian .ant-tabs-nav .ant-tabs-tab .tab-content:hover{
  border-bottom: 3px solid #C4D600 !important;
  padding: 0px 8px 1px 8px;
  color:#404041;
}

.billing-history .billing-history-accordian .tab-content-active{
  border-bottom: 3px solid #C4D600 !important;
  padding: 0px 8px 1px 8px;
}

.billing-history .billing-history-accordian .ant-tabs-nav .ant-tabs-tab-active,
.billing-history .billing-history-accordian .ant-tabs-nav .ant-tabs-tab:hover{
  color:#404041
}



.billing-history{
    background-color: #FFFFFF;
}

.billing-history .ant-tabs .ant-tabs-left-bar {
  background-color:#FFFFFF;
  width: 26%;
  margin: 0px;
  border-radius: 4px 4px 4px 4px;
  box-shadow: inset 0 1px 3px 0 rgba(0,0,0,0.5), 0 2px 4px 0 rgba(0,0,0,0.5);
  z-index: 9999;
}

.billing-history .billing-history-accordian .ant-tabs .ant-tabs-top-bar .ant-tabs-tab:hover {
  height: 60px;
  margin: 0px;
  border-radius: 4px 4px 4px 4px;


  font-family: "Simply Cricket Cond Book", Helvetica, Arial, sans-serif;	
  font-size: 12px;	
  font-weight: 500;	
  line-height: 15px;
}

.billing-history .billing-history-accordian .ant-tabs-nav .ant-tabs-tab{
  margin: 0px;
}

.tab-content-heading {
  font-family: "Simply Cricket Cond Book", Helvetica, Arial, sans-serif;
  font-size: 12px;	
  font-weight: bold;	
  line-height: 12px;

  width: 100%;
  margin-right: 5px;
}

.tab-content-left{
  display: flex;
  justify-content: flex-start;
  flex-direction: row; 
  width: 90%;  
  height: 15px;
}

.bridge-icon{
  margin: -3px 4px 0px 0px;
  color: #3170B7;
}

.suspend-icon{
  height: 10px;
  width: 10px;
  border-radius: 50%;
  background-color: #FFCD00;
  display: inline-block;
  margin: 0px 4px 2px 4px;
  padding: 0px 4px;
}

.disable-suspend-icon{
  height: 10px;
  width: 10px;
  border-radius: 50%;
  display: inline-block;
  margin: 2px;
  padding: 0px 4px;
}

.billing-history .billing-history-header {	
  display: flex;justify-content: space-between; color: #000000;	
  font-family: "Simply Cricket Cond Book", Helvetica, Arial, sans-serif;
  font-size: 24px;	
  font-weight: 500;	
  line-height: 18px;

  padding: 22px 22px 23px 44px;
  border-radius: 0px;
  background-color: white;
  overflow-y: auto;
}

@media screen and (min-width: 100px){

  .header-bottom-styling{
    height: 35px;	
    width: 100%;	
    border-radius: 0;	
    background-color: #F6F6F6;

  }
}

.billing-history .main-table{
  width: 100%;
  height: 465px; 
  overflow-y: auto;
  border-radius: 0px 0px 4px 4px
}

@media screen and (max-width: 700px){
  .billing-history .main-table{
    width: 108%;
  }
}

.billing-history .main-table{
  margin-right: 5%;
}

.billing-history, 
.main-table,
.ant-table-thead > tr > th, 
.ant-table-tbody > tr > td{ 
    border-color: #000000; 
    background-color: white; 
} 

/* This makes amount appear on the right side */

th.billing-history-accountBalance{
  padding-left: 6px !important;
}
.billing-history .main-table th.billing-history-amount,
.billing-history .main-table td.billing-history-amount,
.billing-history .main-table th.billing-history-accountBalance,
.billing-history .main-table td.billing-history-accountBalance{
  text-align: right !important;
}

.main-table .ant-table-thead > tr, 
.ant-table-tbody > tr > td {
  padding: 6px 6px;
}

.billing-history .main-table .ant-table-row-expand-icon{
} 

.billing-history .main-table .hide-icon .ant-table-row-expand-icon-cell .ant-table-row-expand-icon{
  opacity: 0;
  pointer-events: none
}

.billing-history .main-table .ant-table-thead > tr > th{
  padding-left: 6px;
  padding-right: 6px;
} 

.billing-history .main-table .ant-table-tbody > tr > td{
  padding: 3px 6px;  
}

.billing-history .main-table .ant-table-thead > tr > th{
  color: #404041 !important;
  background-color: white;
  font-family: "Simply Cricket Cond Book", Helvetica, Arial, sans-serif;
  font-size: 12px;	
  font-weight: 600;	
  line-height: 18px;
}

.billing-history .dateTime{
  color: #404041;	
  font-family: "Simply Cricket Cond Book", Helvetica, Arial, sans-serif;
  font-size: 12px;	
  font-weight: 500;	
  line-height: 21px;	
}

.billing-history .type{
    color: #6E6F72;	
    font-family: "Simply Cricket Cond Book", Helvetica, Arial, sans-serif;
    font-size: 12px;	
    font-weight: 500;	
    line-height: 18px;
}

/* Styling for Level one table */
.hide-row-data .level-one-ctn-column{
  display: table-column;
}

.billing-history .main-table .ctn-table{
  margin-left: 16%;
}

.billing-history .main-table .ctn-table > .ant-spin-nested-loading > .ant-spin-container > .ant-table {
    margin: 5px;
}

.billing-history .main-table .ant-table-thead > tr
{
  border-bottom: 1px !important;
}
.billing-history .main-table .ctn-table .ant-table-tbody > tr > td, 
.billing-history .main-table .ctn-table .ant-table-thead > tr > th  
{
  border-bottom: 0px !important;
}

/* Targeting ctn level table ->  row of column structuer */
.billing-history .main-table .ctn-table .ant-table-thead > tr > th, .ant-table-tbody > tr > td {
  padding: 0px 6px;
}

.billing-history .main-table .ctn-table .ant-table-thead > tr > th{
  color: #BBBDC0 !important;	
  font-family: "Simply Cricket Cond Book", Helvetica, Arial, sans-serif;
  font-size: 12px;	
  font-style: italic;	
  letter-spacing: 0.1px;	
  line-height: 16px;
}

.billing-history .main-table .ctn-table 
.data-styling-for-ctn-table{
  color: #6E6F72;	
  font-family: "Simply Cricket Cond Book", Helvetica, Arial, sans-serif;
  font-size: 15px;	
  font-style: italic;	
}

.billing-history .main-table .ctn-table .ant-table-tbody > tr > td{
  padding: 0px 6px;  
}

/* Targeting banLevel Table */
.billing-history .main-table .add-styling {
    margin-bottom: 8px;
}


