params.styles:
.c-360-scope-header {
  font-family: "Simply Cricket Bold", Helvetica, Arial, sans-serif;
  height: 100%;
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: space-between;
  background: #404041;
  color: #fff;
  height: 80px;
  border-bottom: 1px solid #979797;
  /* padding-top: 15px; */
}

.c-360-scope-header .title {
  font-weight: 700;
}

.c-360-scope-header .wrapper {
  display: flex;
  justify-content: space-between;
  padding: 0 24px;
  width: 100%;
}
.c-360-scope-header .wrapper__left {
  display: flex;
  justify-content: space-between;
  width: 80%;
}
.c-360-scope-header .wrapper__right {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.c-360-scope-header .title {
  display: flex;
  flex-direction: column;
  font-weight: bold;
}

.c-360-scope-header .title__name {
  display: flex;
  align-items: center;
  font-size: 18px;
  height: 30px;
}

.c-360-scope-header .title__ban {
  font-family: "Simply Cricket Cond Bold", Helvetica, Arial, sans-serif;;
  font-weight: bold;
  display: flex;
  align-items: center;
  font-size: 12px;
  height: 20px;
}

.c-360-scope-header .title__number {
  font-size: 14px;
}

.c-360-scope-header .active {
  width: 20px;
  height: 20px;
  border-radius: 50%;
  margin: 5px 0;
  background-color: rgb(109, 109, 109);
}

.c-360-scope-header__circle {
  display: flex;
  justify-content: center;
  align-items: center;
  width: 62px;
  height: 62px;
  border-radius: 100%;
  /* margin: 5px 0; */
  background-color: #fff;
  animation: blink 1.5s infinite;
}

@keyframes blink {
	0% {opacity: 0}
	49%{opacity: 0}
	50% {opacity: 1}
}

.c-360-scope-header .status-container {
  display: flex;
  flex-direction: column;
  height: 100%;
  align-items: center;
}

/* Marks */
.c-360-scope-header .A-mark {
  background-color: #60a630;
}

.c-360-scope-header .O-mark {
  background-color: #60a630;
}

.c-360-scope-header .S-mark {
  background-color: #ffd600;
}

.c-360-scope-header .T-mark {
  background-color: #12a6f0;
}

.c-360-scope-header .C-mark {
  background-color: #f01612;
}

.c-360-scope-header .N-mark {
  background-color: #f01612;
}

/* Amount Due, Bridge Pay, Auto Pay */
.c-360-scope-header .display {
  margin-left: 10px;
  margin-right: 10px;
}


/* Display Top */
.c-360-scope-header .display-top {
  display: flex;
  align-items: center;
  justify-content: flex-start;
  font-family: "Simply Cricket Medium", Helvetica, Arial, sans-serif;;
  height: 30px;
  line-height: 21px;
  font-weight: 500;
  font-size: 18px;

}

.c-360-scope-header .display-top--now {
  color: #f01612;
}

.c-360-scope-header .display-top__phone {
  display: flex;
  height: 30px;
  justify-content: center;
  align-items: center;
