} /* BAN Styling */
.refill-card-payment .ban-container {
    display: none;
} /* Header Styling */
.payment-form-header {
    height: 43px;
    color: #6e6f72;
    font-family: "Simply Cricket Medium", Helvetica, Arial, sans-serif;
    font-size: 16px;
    font-weight: 300;
    line-height: 21px;
    margin: 28px 0px 0px 46.5px;
}

/* Main From*/
.refill-card-payment {
    margin: 0px 0px 0px 46.5px;
    width: 700px;
    display: flex;
    flex-wrap: wrap;
    /* align-items: baseline; */
}

/* Payment card Number & card Name*/
.refill-card-payment .card-name-container {
    width: 47.5%;
    padding-right: 2.5%;
}

.refill-card-payment .card-number-container {
    width: 47.5%;
    margin-right: 54.5%;
}

.refill-card-payment .card-number-container .card-number-label,
.refill-card-payment .card-payment-amount-container .card-payment-amount-label,
.refill-card-payment .card-payment-fee-container .card-payment-fee-label,
.refill-card-payment .card-payment-total-container .card-payment-total-label {
    color: #6e6f72;
    font-family: "Simply Cricket Medium", Helvetica, Arial, sans-serif;
    font-size: 12px;
    font-weight: 500;
    line-height: 17px;
    padding-left: 5px;
    width: 112%;
}

.refill-card-payment .card-number-container input#refill_card_security_pin {
    box-sizing: border-box;
    height: 37px;
    width: 100%;
    border: 1px solid #bbbdc0;
    border-radius: 5px;
    background-color: #ffffff;
}

/* Error border for card name and card number  */
.refill-card-payment .card-number-container .has-error input#refill_card_security_pin {
    border-color: #cf292a !important;
}

/* Amount, fee and total  Spacing*/
.refill-card-payment .card-payment-amount-container {
    width: 22.5%;
    padding-right: 1.25%;
}

.refill-card-payment .card-payment-fee-container {
    width: 22.5%;
    padding-left: 1.25%;
    margin-right: 2.5%;
}

.refill-card-payment .card-payment-total-container {
    margin-left: 2.5%;
    padding-right: 1.25%;
    width: 22.5%;
}

/* Payment card cvv and zip and amount and fee and total*/
.refill-card-payment .card-payment-amount-container input#refill_payment_amount,
.refill-card-payment .card-payment-total-container input#refill_amount_total {
    box-sizing: border-box;
    border: 1px solid #bbbdc0;
    height: 37px;
    width: 100%;
    border-radius: 5px;
    background-color: #ffffff;
}

/* Error Border */
.refill-card-payment .card-payment-amount-container .has-error input#refill_payment_amount,
.refill-card-payment .card-payment-total-container .has-error input#refill_amount_total {
    border-color: #cf292a !important;
}

.refill-card-payment .card-payment-fee-container input#refill_card_fee {
    box-sizing: border-box;
    height: 37px;
    width: 100%;
    border: 1px solid #bbbdc0;
    border-radius: 5px;
  }

/* Error Message*/
.refill-card-payment .card-number-container .card-number-item .ant-form-explain,
.refill-card-payment .card-payment-amount-container .card-payment-amount-item .ant-form-explain,
.refill-card-payment .card-payment-total-container .card-payment-total-item .ant-form-explain {
    color: #cf292a;
    font-family: "Simply Cricket Cond";
    font-size: 12px;
    font-weight: 500;
    line-height: 17px;
    width: 110%;
}
/* CTN Select */

.refill-card-payment .ctn-label-select-container {
    width: 40%;
    padding-right: 1.25%;
    margin-top: -3px;
    margin-right: 40%;
}

.refill-card-payment .ctn-label-select-container .ant-form-explain,
.refill-card-payment .ctn-label-select-container .ctn-label {
    color: #6e6f72;
    font-family: "Simply Cricket Medium", Helvetica, Arial, sans-serif;
    font-size: 12px;
    font-weight: 500;
    line-height: 17px;
}

.refill-card-payment .ctn-label-select-container .ant-form-explain {
    margin-top: 2px;
}

.refill-card-payment .ctn-label-select-container .ctn-item svg {
    fill: #6e6f72;
}

.refill-card-payment .ctn-label-select-container .ctn-item .ant-select-selection.ant-select-selection--single {
    height: 37px;
    width: 100%;
    border: 1px solid #bbbdc0;
    border-radius: 5px;
    background-color: #ffffff;
}

/* error border for select */
.refill-card-payment .ctn-label-select-container .ctn-item .has-error .ant-select-selection.ant-select-selection--single {
    border: 1px solid #cf292a !important;
}

.refill-card-payment .ctn-label-select-container .ctn-item .ant-select-selection-selected-value {
    padding: 8px 15px 6px 15px;
    color: #b3b3b3;
    font-family: "Simply Cricket Medium", Helvetica, Arial, sans-serif;
    font-size: 15px;
    font-weight: 500;
    line-height: 22px;
}

.refill-card-payment .ctn-label-select-container .ctn-item .ant-select-selection__placeholder {
    color: #b3b3b3;
    font-family: "Simply Cricket Medium", Helvetica, Arial, sans-serif;
    font-size: 15px;
    font-weight: 500;
    line-height: 22px;
    padding: 3px 15px 6px 15px;
}

/* Form Button styling */
.payment-form-verify-button {
    box-sizing: border-box;
    font-family: "Simply Cricket Medium", Helvetica, Arial, sans-serif;
    height: 37px;
    width: 140px;
    border: 1px solid #339ce1;
    border-radius: 5px;
    background-color: #3170b7;
    padding: 4px 0px 0px 0px;
    box-shadow: 0 1px 2px 0 #9fb8c8;
}

.submit-button-form-item {
    padding-left: 47.5%;
    margin-top: 7%;
    margin-bottom: 10%;
}
